package datatype;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String place = "Brooklyn";
		int start = 1;
		
		do
		{
			System.out.println("I am from" + place);
			start ++;
						
		}while(start<=3);
		
		

	}

}
